        public readonly string ConsumerSecret;

        [OutputConstructor]
        private FunctionAppAuthSettingsTwitter(
            string consumerKey,
            string consumerSecret)
        {
            ConsumerKey = consumerKey;
            ConsumerSecret = consumerSecret;
        }
    }

    [OutputType]
    public sealed class FunctionAppConnectionStrings
    {
        /// <summary>
        /// The name of the Connection String.
        /// </summary>
        public readonly string Name;
        /// <summary>
        /// The type of the Connection String. Possible values are `APIHub`, `Custom`, `DocDb`, `EventHub`, `MySQL`, `NotificationHub`, `PostgreSQL`, `RedisCache`, `ServiceBus`, `SQLAzure` and  `SQLServer`.
        /// </summary>
        public readonly string Type;
        /// <summary>
        /// The value for the Connection String.
        /// </summary>
        public readonly string Value;

        [OutputConstructor]
        private FunctionAppConnectionStrings(
            string name,
            string type,
            string value)
        {
            Name = name;
            Type = type;
            Value = value;
        }
    }

    [OutputType]
    public sealed class FunctionAppIdentity
    {
        /// <summary>
        /// Specifies a list of user managed identity ids to be assigned. Required if `type` is `UserAssigned`.
        /// </summary>
        public readonly ImmutableArray<string> IdentityIds;
        /// <summary>
        /// The Principal ID for the Service Principal associated with the Managed Service Identity of this App Service.
        /// </summary>
        public readonly string PrincipalId;
        /// <summary>
        /// The Tenant ID for the Service Principal associated with the Managed Service Identity of this App Service.
        /// </summary>
        public readonly string TenantId;
        /// <summary>
        /// Specifies the identity type of the Function App. Possible values are `SystemAssigned` (where Azure will generate a Service Principal for you), `UserAssigned` where you can specify the Service Principal IDs in the `identity_ids` field, and `SystemAssigned, UserAssigned` which assigns both a system managed identity as well as the specified user assigned identities.
        /// </summary>
        public readonly string Type;

        [OutputConstructor]
        private FunctionAppIdentity(
            ImmutableArray<string> identityIds,
            string principalId,
            string tenantId,
            string type)
        {
            IdentityIds = identityIds;
            PrincipalId = principalId;
            TenantId = tenantId;
            Type = type;
        }
    }

    [OutputType]
    public sealed class FunctionAppSiteConfig
    {
        /// <summary>
        /// Should the Function App be loaded at all times? Defaults to `false`.
        /// </summary>
        public readonly bool? AlwaysOn;
        /// <summary>
        /// A `cors` block as defined below.
        /// </summary>
        public readonly FunctionAppSiteConfigCors Cors;
        /// <summary>
        /// State of FTP / FTPS service for this function app. Possible values include: `AllAllowed`, `FtpsOnly` and `Disabled`.
        /// </summary>
        public readonly string FtpsState;
        /// <summary>
        /// Specifies whether or not the http2 protocol should be enabled. Defaults to `false`.
        /// </summary>
        public readonly bool? Http2Enabled;
        /// <summary>
        /// A list of objects representing ip restrictions as defined below.
        /// </summary>
        public readonly ImmutableArray<FunctionAppSiteConfigIpRestrictions> IpRestrictions;
        /// <summary>
        /// Linux App Framework and version for the AppService, e.g. `DOCKER|(golang:latest)`.
        /// </summary>
        public readonly string LinuxFxVersion;
        /// <summary>
        /// The minimum supported TLS version for the function app. Possible values are `1.0`, `1.1`, and `1.2`. Defaults to `1.2` for new function apps.
        /// </summary>
        public readonly string MinTlsVersion;
        /// <summary>
        /// Should the Function App run in 32 bit mode, rather than 64 bit mode? Defaults to `true`.
        /// </summary>
        public readonly bool? Use32BitWorkerProcess;
        /// <summary>
        /// Should WebSockets be enabled?
        /// </summary>
        public readonly bool? WebsocketsEnabled;

        [OutputConstructor]
        private FunctionAppSiteConfig(
            bool? alwaysOn,
            FunctionAppSiteConfigCors cors,
            string ftpsState,
            bool? http2Enabled,
            ImmutableArray<FunctionAppSiteConfigIpRestrictions> ipRestrictions,
            string linuxFxVersion,
            string minTlsVersion,
            bool? use32BitWorkerProcess,
            bool? websocketsEnabled)
        {
            AlwaysOn = alwaysOn;
            Cors = cors;
            FtpsState = ftpsState;
            Http2Enabled = http2Enabled;
            IpRestrictions = ipRestrictions;
            LinuxFxVersion = linuxFxVersion;
            MinTlsVersion = minTlsVersion;
            Use32BitWorkerProcess = use32BitWorkerProcess;
            WebsocketsEnabled = websocketsEnabled;
        }
    }

    [OutputType]
    public sealed class FunctionAppSiteConfigCors
    {
        /// <summary>
        /// A list of origins which should be able to make cross-origin calls. `*` can be used to allow all calls.
        /// </summary>
        public readonly ImmutableArray<string> AllowedOrigins;
        /// <summary>
        /// Are credentials supported?
        /// </summary>
        public readonly bool? SupportCredentials;

        [OutputConstructor]
        private FunctionAppSiteConfigCors(
            ImmutableArray<string> allowedOrigins,
            bool? supportCredentials)
        {
            AllowedOrigins = allowedOrigins;
            SupportCredentials = supportCredentials;
        }
    }

    [OutputType]
    public sealed class FunctionAppSiteConfigIpRestrictions
    {
        /// <summary>
